import React, { useEffect } from "react";
import styles from './TestReview.module.scss';
import { useParams } from "react-router-dom";
import useTestStore from "../../stores/testStore";
import {BiSolidComment} from 'react-icons/bi';

export default function TestReview() {
    const params = useParams();
    const testId = params.id;
    const test = useTestStore(state => state.test);
    const getTest = useTestStore(state => state.getTest);

    useEffect(() => {
        if (testId) {
            getTest(testId);
        }
    })
    return (
        <>
            {test ? <div className={styles.testContainer}>
                <h1>{test.name}</h1>
                <h3>{test.description}</h3>
                <h5>Total questions: {test.questions.length}</h5>
                <div className={styles.testOtherData}>
                            <p className={styles.testLikes}>
                                <span onClick={(e: React.MouseEvent<HTMLParagraphElement>) => handleLike(e, test._id)} style={{ color: 'red' }}>&#10084;</span>
                                    {test.likes}
                                </p>
                            <p className={styles.testComments}>
                                <BiSolidComment color='#2065ce' className={styles.testCommentIcon} />
                                {test.comments.length}
                            </p>
                        </div>
            </div> : <h1>Loading...</h1>}
        </>
    );
}
