import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TestPage.module.scss';
import { useSearchParams } from 'react-router-dom';

interface Answer {
    answer: string;
    isRight: boolean;
    points: 0 | 1 | 2 | 3;
}

interface Question {
    question: string;
    answers: Answer[];
}

interface Result {
    result: string;
    minScore: number;
    maxScore: number;
}

interface Test {
    name: string;
    description: string;
    questions: Question[];
    results: Result[];
    score: number;
    likes: number;
    comments: string[];
    createdAt: Date;
    isUpdated: Date[];
}



export default function TestPage() {
    const [test, setTest] = useState<Test | null>(null);
    const [userAnswers, setUserAnswers] = useState<{ [key: number]: number }>({});
    const [score, setScore] = useState<number>(0);
    const [searchParams, setSearchParams] = useSearchParams();
    const testId = searchParams.get('id');
    const id = searchParams.getAll('id');  console.log(id)

    useEffect(() => {
        // Fetch the test data
        axios.get(`/api/tests/${testId}`)
            .then(response => {
                setTest(response.data);
            })
            .catch(error => {
                console.error('There was an error fetching the test!', error);
            });
    }, [testId]);

    const handleAnswerChange = (questionIndex: number, points: number) => {
        setUserAnswers(prev => ({
            ...prev,
            [questionIndex]: points
        }));
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        const totalScore = Object.values(userAnswers).reduce((acc, points) => acc + points, 0);
        setScore(totalScore);
    };
  
    if (!test) return <div>Loading...{testId}</div>;

    return (
        <div className="test-page">
            <h1>{test.name}</h1>
            <p>{test.description}</p>

            <form onSubmit={handleSubmit}>
                {test.questions.map((question, qIndex) => (
                    <div key={qIndex} className="question">
                        <h3>{question.question}</h3>
                        {question.answers.map((answer, aIndex) => (
                            <div key={aIndex} className="answer">
                                <input
                                    type="radio"
                                    name={`question-${qIndex}`}
                                    value={answer.points}
                                    onChange={() => handleAnswerChange(qIndex, answer.points)}
                                />
                                <label>{answer.answer}</label>
                            </div>
                        ))}
                    </div>
                ))}

                <button type="submit">Submit</button>
            </form>

            {score !== 0 && (
                <div className="results">
                    <h2>Your Score: {score}</h2>
                    {test.results.map((result, index) => (
                        score >= result.minScore && score <= result.maxScore ? (
                            <div key={index}>
                                <h3>{result.result}</h3>
                            </div>
                        ) : null
                    ))}
                </div>
            )}
        </div>
    );
};
