import { create } from 'zustand';
import { devtools } from 'zustand/middleware';
import { immer } from 'zustand/middleware/immer';
import API from './../api/axiosConfig';
import { TestPageFormValues } from '../pages/testPage/TestPage';

export type Answer = {
    answer: string,
    points: number
}

export interface IQuestion {
    question: string,
    answers: Answer[]
}

export type Result = {
    result: string,
    minScore: number,
    maxScore: number
}

export interface IComment {
    comment: string,
    author: string,
    isAnswer: {
        isAnswer: boolean,
        parentComment: string,
    }
    likes: number,
    answers: IComment[]
    createdAt: string
}

export interface Test {
    _id: string,
    name: string,
    description: string,
    author: {
        _id: string,
        username: string
    },
    questions: IQuestion[],
    results: Result[],
    score: number,
    likes: number,
    comments: string[],
    createdAt: string,
    isUpdated: [],
    __v: number
}

interface IStore {
    test: Test | null,
    result: string,
    score: number,
    comments: IComment[],
    formError: string,
    createdTestId: string,
    getTest: (testId: string) => void,
    createTest: (name: string, description: string, questions: IQuestion[], results: Result[], score: number) => void,
    getComments: () => void,  // I have to create backend endpoints for this function
    calculateResult: (data: TestPageFormValues) => void,
    setFormError: (err: string) => void,
    setScore: (num: number) => void,
}


const useTestStore = create<IStore>()(devtools(immer((set, get) => ({
    test: null,
    result: '',
    formError: '',
    comments: [],
    score: 0,
    createdTestId: '',
    getTest: async (testId: string) => {
        API.get(`/test/${testId}`).then(res => {
            set({ test: res.data.test });
        })
    },
    createTest: async (name: string, description: string, questions: IQuestion[], results: Result[], score: number) => {
        await API.post(`test`, { name, description, questions, results, score }).then(res => set({ createdTestId: res.data.id }));
    },
    getComments: async () => {
        await API.get(`/comment/${get().test?._id}`).then(res => {
            set({comments: res.data.comments})
        }).catch(err => console.log(err));
    },
    calculateResult: (data) => {
        let score = 0;

        data.questions.forEach(q => {
            if (q.selectedAnswer) {
                score += Number(q.selectedAnswer);
            }
        });

        const currentTest = get().test;

        if (currentTest !== null) {
            let computedResult = '';

            for (let i = 0; i < currentTest.results.length; i++) {
                const res = currentTest.results[i];
                if (score >= res.minScore && score <= res.maxScore) {
                    computedResult = res.result;
                    break;
                }
            }

            set({ result: computedResult });
        }
    },
    setFormError: (err: string) => {
        set({ formError: err });
    },
    setScore: (num: number) => {
        set({ score: num });
    }
}))));

export default useTestStore;
