import React from 'react';
import styles from './TestForm.module.scss';
import { Answer, IQuestion, Result, Test } from '../../stores/testStore';
import { useForm, SubmitHandler, useFieldArray, Controller } from 'react-hook-form';

/*name: '',
description: '',
author: '',
questions: [],
results: [],
score: 0,
likes: 0,
comments: [],
createdAt: new Date(),
isUpdated: []*/

type FormValues = {
    name: string,
    description: string,
    questions: IQuestion[],
    results: Result[]
}


export default function TestForm() {
    const { register, control, handleSubmit } = useForm<FormValues>({
        mode: 'onBlur'
    });

    const { fields: questionFields, append: appendQuestion } = useFieldArray({
        control,
        name: 'questions'
    });
    const { fields: resultFields, append: appendResult } = useFieldArray({
        control,
        name: 'results'
    });

    const onSubmit: SubmitHandler<FormValues> = (data) => {
        console.log(data);
    }

    const addQuestion = () => {
        appendQuestion({
            question: '',
            answers: [
                { answer: '', isRight: false, points: 0 },
                { answer: '', isRight: false, points: 0 },
                { answer: '', isRight: false, points: 0 },
                { answer: '', isRight: false, points: 0 }
            ]
        });
    };

    return (
        <form className={styles.form} onSubmit={handleSubmit(onSubmit)}>
            <div className={styles.formGroup}>
                <label htmlFor="name">Name</label>
                <input type="text" id="name" {...register('name', { required: 'This field is required' })} />
            </div>

            <div className={styles.formGroup}>
                <label htmlFor="description">Description</label>
                <textarea id="description" {...register('description', { required: 'This field is required' })} />
            </div>

            <div className={styles.formGroup}>
                <label>Questions</label>
                {questionFields.map((field, index) => (
                    <div key={field.id} className={styles.nestedFormGroup}>
                        <label htmlFor={`questions[${index}].question`}>Question</label>
                        <input id={`questions[${index}].question`} {...register(`questions.${index}.question`, { required: true })} />

                        <label>Answers</label>
                        {field.answers.map((answer, aIndex) => (
                            <div key={aIndex} className={styles.answerGroup}>
                                <input
                                    type="text"
                                    {...register(`questions.${index}.answers.${aIndex}.answer`, { required: true })}
                                    placeholder={`Answer ${aIndex + 1}`}
                                />
                                <label>
                                    <input
                                        type="radio"
                                        {...register(`questions.${index}.answers.${aIndex}.isRight`)}
                                        value="true"
                                    />
                                    Correct
                                </label>
                                <select {...register(`questions.${index}.answers.${aIndex}.points`, { required: true })}>
                                    <option value="0">0</option>
                                    <option value="1">1</option>
                                    <option value="2">2</option>
                                    <option value="3">3</option>
                                </select>
                            </div>
                        ))}
                    </div>
                ))}
                <button type="button" onClick={() => addQuestion()}>Add question</button>
            </div>


            <button type="button">Add result</button>
            <button type="submit">Submit</button>
        </form >
    );
};
