import React, { useState } from 'react';
import './Pagination.scss';

const Pagination = ({ totalPages }) => {
    const [currentPage, setCurrentPage] = useState(1);

    const handlePageChange = (page) => {
        if (page >= 1 && page <= totalPages) {
            setCurrentPage(page);
        }
    };

    const renderPageNumbers = () => {
        const pageNumbers = [];
        const maxPageNumbersToShow = 5;
        const startPage = Math.max(currentPage - Math.floor(maxPageNumbersToShow / 2), 1);
        const endPage = Math.min(startPage + maxPageNumbersToShow - 1, totalPages);

        if (startPage > 1) {
            pageNumbers.push(
                <button key={1} className={`pagination__button ${currentPage === 1 ? 'active' : ''}`} onClick={() => handlePageChange(1)}>
                    1
                </button>
            );
            if (startPage > 2) {
                pageNumbers.push(<span key="ellipsis-start" className="pagination__ellipsis">...</span>);
            }
        }

        for (let i = startPage; i <= endPage; i++) {
            pageNumbers.push(
                <button key={i} className={`pagination__button ${currentPage === i ? 'active' : ''}`} onClick={() => handlePageChange(i)}>
                    {i}
                </button>
            );
        }

        if (endPage < totalPages) {
            if (endPage < totalPages - 1) {
                pageNumbers.push(<span key="ellipsis-end" className="pagination__ellipsis">...</span>);
            }
            pageNumbers.push(
                <button key={totalPages} className={`pagination__button ${currentPage === totalPages ? 'active' : ''}`} onClick={() => handlePageChange(totalPages)}>
                    {totalPages}
                </button>
            );
        }

        return pageNumbers;
    };

    return (
        <div className="pagination">
            <button
                className="pagination__button"
                onClick={() => handlePageChange(currentPage - 1)}
                disabled={currentPage === 1}
            >
                &laquo;
            </button>
            {renderPageNumbers()}
            <button
                className="pagination__button"
                onClick={() => handlePageChange(currentPage + 1)}
                disabled={currentPage === totalPages}
            >
                &raquo;
            </button>
        </div>
    );
};

export default Pagination;
