import React, { useEffect } from 'react';
import styles from './Comments.module.scss';
import useTestStore from '../../stores/testStore';
import { SubmitHandler, useForm } from 'react-hook-form';

type FormValues = {
    comment: string
}

export default function Comments() {
    const getComments = useTestStore(state => state.getComments);
    const getAnswers = useTestStore(state => state.getAnswers);
    const comments = useTestStore(state => state.comments);
    const {register, handleSubmit, formState: {errors}} = useForm<FormValues>();

    useEffect(() => {
        getComments();
    }, [getComments]);

    const onSubmit: SubmitHandler<FormValues> = (data) => {

    }

    return (
        <div className={styles.commentsSection}>
            <h2>Comments</h2>
            <ul className={styles.commentsList}>
                {comments.map((comment) => (
                    <li key={comment._id} className={styles.comment}>
                        <p>{comment.comment}</p>
                        <span>â€” {comment.author}</span>
                    </li>
                ))}
            </ul>
            <div className={styles.formSection}>
                <h3>Add a Comment</h3>
                <form onSubmit={handleSubmit(onSubmit)}>
                <textarea
                    placeholder="Your comment"
                    {...register('comment', {required: 'This field is required'})}
                    className={styles.textarea}
                />
                <button type='submit'>
                    Add Comment
                </button>
                </form>
            </div>
        </div>
    );
};
