import { create } from 'zustand';
import { devtools } from "zustand/middleware";
import { immer } from 'zustand/middleware/immer';
import API from './../api/axiosConfig';
import { Test } from './testStore';


interface TestData extends Test {
    authorsName: string,
}

interface IStore {
    tests: TestData[] | null,
    searchVal: string
    getTests: () => void,
    handleSearchVal: (val: string) => void,
}

const useHomeStore = create<IStore>()(devtools(immer((set, get) => ({
    tests: null,
    searchVal: '',
    getTests: async () => {
        await API.get(`/tests`).then(res => {
            set({ tests: res.data.tests });
        })
    },
    handleSearchVal: (val: string) => {
        set({searchVal: val});
    }
}))));

export default useHomeStore;
