import { create } from 'zustand';
import { devtools } from 'zustand/middleware';
import { immer } from 'zustand/middleware/immer';
import API from './../api/axiosConfig';
import { TestPageFormValues } from '../pages/testPage/TestPage';

export type Answer = {
    answer: string,
    points: number
}

export interface IQuestion {
    question: string,
    answers: Answer[]
}

export type Result = {
    result: string,
    minScore: number,
    maxScore: number
}

export interface IComment {
    comment: string,
    author: string,
    likes: number,
    createdAt: string
}

export interface Test {
    _id: string,
    name: string,
    description: string,
    author: string,
    questions: IQuestion[],
    results: Result[],
    score: number,
    likes: number,
    comments: string[],
    createdAt: string,
    isUpdated: [],
    __v: number
}

interface IStore {
    test: Test | null,
    result: string,
    score: number,
    comments: IComment[],
    formError: string,
    getTest: (testId: string) => void,
    submitTest: () => void,
    getComments: () => void,  // I have to create backend endpoint for this function
    calculateResult: (data: TestPageFormValues) => void,
    setFormError: (err: string) => void,
    setScore: (num: number) => void
}


const useTestStore = create<IStore>()(devtools(immer((set, get) => ({
    test: null,
    result: '',
    formError: '',
    comments: [],
    score: 0,
    getTest: async (testId: string) => {
        API.get(`/test/${testId}`).then(res => {
            set({ test: res.data.test });
        })
    },
    submitTest: async () => {

    },
    getComments: async () => {

    },
    calculateResult: (data) => {
        let score = 0;

        data.questions.forEach(q => {
            if (q.selectedAnswer) {
                score += Number(q.selectedAnswer);
            }
        });

        const currentTest = get().test;

        if (currentTest !== null) {
            let computedResult = '';

            for (let i = 0; i < currentTest.results.length; i++) {
                const res = currentTest.results[i];
                if (score >= res.minScore && score <= res.maxScore) {
                    computedResult = res.result;
                    break;
                }
            }

            set({ result: computedResult });
        }
    }, 
    setFormError: (err: string) => {
        set({ formError: err });
    },
    setScore: (num)
}),
),
),
);

export default useTestStore;
