import React, { useEffect } from 'react';
import styles from './TestPage.module.scss';
import { NavLink, useParams } from 'react-router-dom';
import useTestStore, { Answer } from '../../stores/testStore';
import { useForm, Controller } from "react-hook-form";

type Question = {
    question: string;
    answers: Answer[];
    selectedAnswer: string;
}

export type TestPageFormValues = {
    questions: Question[]
}

export default function TestPage() {
    const getTest = useTestStore(state => state.getTest);
    const test = useTestStore(state => state.test);
    const result = useTestStore(state => state.result)
    const calculateResult = useTesWtStore(state => state.calculateResult)
    const params = useParams();
    const testId = params.id;
    const { control, handleSubmit, setValue } = useForm<TestPageFormValues>({
        defaultValues: {
            questions: test ? test.questions.map(question => ({
                question: question.question,
                answerw: question.answers,
                selectedAnswer: ''
            })) : []
        }
    });


    useEffect(() => {
        if (testId) {
            getTest(testId)
        }
    }, [testId, getTest]);

    useEffect(() => {
        if (test) {
            setValue("questions", test.questions.map(q => ({
                question: q.question,
                answers: q.answers,
                selectedAnswer: ''
            })));
        }
    }, [test, setValue]);

    const onSubmit = (data: TestPageFormValues) => {
        calculateResult(data);
    }

    return (
        test ?
            <div className={styles.testPage} >
                <h1>{test.name}</h1>
                <p>{test.description}</p>

                <form onSubmit={handleSubmit(onSubmit)}>
                    {test.questions.map((question, qIndex) => (
                        <div key={qIndex} className={styles.question}>
                            <h3>{question.question}</h3>
                            {question.answers.map((answer, aIndex) => (
                                <div key={aIndex} className={styles.answer}>
                                    <Controller name={`questions.${qIndex}.selectedAnswer`}
                                        control={control}
                                        render={({ field }) => (
                                            <input
                                                type="radio"
                                                name={`question-${qIndex}`}
                                                value={answer.points}
                                                checked={field.value === answer.points}
                                                onChange={() => field.onChange(answer.points)}
                                            />
                                        )}
                                    />
                                    <label>{answer.answer}</label>
                                </div>
                            ))}
                        </div>
                    ))}

                    <button type="submit"><NavLink className={styles.navLink} to={`/test/${test._id}/result`}>Submit</NavLink></button>
                </form>
            </div >
            : <div className={styles.testPage}><h1>Loading...</h1></div>
    );
};
