import React from 'react';
import styles from './Registration.module.scss';
import useAuthStore from '../../stores/authStore';
import { useForm } from 'react-hook-form';

type FormValues = {
    username: string,
    email: string,
    password: string,
    confirmPassword: string
}


const Registration = () => {
    const reg = useAuthStore(state => state.register);
    const { register, handleSubmit, formState: { errors } } = useForm<FormValues>();

    return (
        <div className={styles.main}>
            <div className={styles.formContainer}>
                <h2 className={styles.header}>Sign Up</h2>
                <form>
                    <div className={styles.inputField}>
                        <label className={styles.label} htmlFor='username'>Username</label>
                        <input {...register('username', {minLength: 3, maxLength: 24, required: true})} 
                        className={styles.input} type='text' id='username' name='username' required />
                    </div>
                    <div className={styles.inputField}>
                        <label className={styles.label} htmlFor='email'>Email</label>
                        <input {...register('email', {minLength: 3, maxLength: 64, required: true})}
                        className={styles.input} id='email' type='email' name='email' required />
                    </div>
                    <div className={styles.inputField}>
                        <label className={styles.label} htmlFor='password'>Password</label>
                        <input {...register('password', {minLength: {value: 8, message: 'Password must contain at least 8 characters'}
                        , maxLength: {value: 64 , message: 'Password can contain maximum 64 characters'}, required: true})}
                        className={styles.input} type='password' id='password' name='password' required />
                    </div>
                    <div className={styles.inputField}>
                        <label className={styles.label} htmlFor='confirmPassword'>Confirm Password</label>
                        <input {...register('confirmPassword', {minLength: {value: 8 , message: 'Passwords not match'}, 
                        maxLength: {value: 64 , message: 'Passwords not match'}, required: true})}
                        className={styles.input} type='password' id='confirmPassword' name='confirmPassword' required />
                    </div>
                    <button onClick={() => reg('assadd@sadasasadsdsdsadsasad', 'asdaasdsdsadsadsssadsdsasdasdsad', 'asdassda')} className={styles.button} type='button'>Sign Up</button>
                </form>
            </div>
        </div>
    );
};

export default Registration;
