import create from 'zustand';
import { persist, devtools } from 'zustand/middleware';
import { immer } from 'zustand/middleware/immer';
import API from './../api/axiosConfig';
import { AxiosResponse } from 'axios';

interface ISTORE {
    usernameField: string,
    emailField: string,
    passwordField: string,
    checkPasswordField: string,
    register: (email: string, username: string, password: string) => void,
    login: (email: string, password: string) => void
}

const useAuthStore = create<ISTORE>()(devtools(immer(persist((set, get) => ({
    usernameField: '',
    emailField: '',
    passwordField: '',
    checkPasswordField: '',
    register: async (email: string, username: string, password: string) => {
        await API.post(`/auth/register`, { email, username, password }).then((data: AxiosResponse<any>) => {
            console.log(data);
        });
    },
    login: async (email: string, password: string) => {
        await API.post(`/auth/login`, { email: 'example@mail.ru', password: '567asd111' }).then((data: AxiosResponse<any>) => {
            console.log(data);
        });
    }
}),
    {
        name: 'auth-store'
    }
),
),
),
);

export default useAuthStore;
