import { create } from 'zustand';
import { devtools } from 'zustand/middleware';
import { immer } from 'zustand/middleware/immer';
import API from './../api/axiosConfig';
import { AxiosResponse } from 'axios';
import { Test } from './testStore';

interface IStore {
    errText: string,
    isLogged: boolean,
    isRegistered: boolean,
    user: IUser | null,
    tests: Test[]
    register: (email: string, username: string, password: string) => void,
    login: (email: string, password: string) => void,
    setError: (err: string) => void,
    logout: () => void,
    refresh: () => void,
    getUserTests: () => void,
    handleIsLogged: (val: boolean) => void,
    handleIsRegistered: (val: boolean) => void

}

interface IUser {
    _id: string,
    username: string,
    email: string,
    isActivated: boolean,
    likedPosts: [],
    likedComments: [],
    createdTests: [],
    posts: [],
    __v: number,
}

const useAuthStore = create<IStore>()(devtools(immer((set, get) => ({
    errText: '',
    isLogged: false,
    isRegistered: false,
    user: null,
    tests: [],
    register: async (email: string, username: string, password: string) => {
        await API.post(`/auth/register`, { email, username, password }).then(res => {
            if (res.data.message === 'Success') {
                set({ isRegistered: true });
            }
        });
    },
    login: async (email: string, password: string) => {
        await API.post(`/auth/login`, { email, password }).then((res: AxiosResponse<any>) => {
            if (res.data.accessToken) {
                localStorage.setItem('token', res.data.accessToken);
                set({ isLogged: true, user: res.data.user });
            } else {
                set({ errText: 'Invalid email or password' });
                set({ isLogged: false });
            }

        });
    },
    logout: async () => {
        await API.post(`/auth/logout`).then(res => {
            console.log(res)
            if (res.data.message === 'Success') {
                localStorage.removeItem('token');
                set({ isLogged: false });
            } else {
                console.log('something went wrong')
            }
        });
    },
    refresh: async () => {
        await API.get(`/auth/refresh`).then(res => {
            if (res.data.accessToken) {
                localStorage.setItem('token', res.data.accessToken);
                set({ isLogged: true, user: res.data.user });
            } else {
                set({ isLogged: false });
            }
        }).catch(err => console.log(err))
    },
    getUserTests: async () => {
        await API.get(`/tests/${get().user?._id}`).then(res => {
            console.log(res.data)
            set({tests: res.data.tests});
        })


    },
    setError: (err: string) => {
        set({ errText: err });
    },
    handleIsLogged: (val: boolean) => {
        set({ isLogged: val });
    },
    handleIsRegistered: (val: boolean) => {
        set({ isRegistered: val });
    },
})
),
),
);

export default useAuthStore;
