import React from 'react';
import styles from './Login.module.scss';
import useAuthStore from '../../stores/authStore';
import { useForm, SubmitHandler } from 'react-hook-form';

type FormValues = {
    email: string,
    password: string,
}

const Login = () => {
    const login = useAuthStore(state => state.login);
    const setError = useAuthStore(state => state.setError);
    const err = useAuthStore(state => state.errText);

    const { register, handleSubmit, formState: { errors } } = useForm<FormValues>();

    const onSubmit: SubmitHandler<FormValues> = (data) => {
        if (data.email.split('@')[1].length < 5 || !data.email.split('@')[1].includes('.') || data.password.length < 8) {
            setError('Entered invalid password or email');
        } else if (errors.email?.message || errors.password?.message) {
            setError('Entered invalid password or email');
        } else {
            login(data.email, data.password);
        }
    }

    return (
        <div className={styles.main}>
            <div className={styles.formContainer}>
                <h2 className={styles.header}>Login</h2>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className={styles.inputField}>
                        <label className={styles.label} htmlFor='email'>Email</label>
                        <input className={styles.input} id='email' name='email' required />
                    </div>
                    <div className={styles.inputField}>
                        <label className={styles.label} htmlFor='password'>Password</label>
                        <input className={styles.input} type='password' id='password' name='password' required />
                    </div>
                    <button onClick={() => login('asdsad', 'asdas')} className={styles.button} type='button'>Login</button>
                </form>
            </div>
            s</div>
    );
};


export default Login;
