import React, { useEffect } from 'react';
import styles from './TestPage.module.scss';
import { useParams } from 'react-router-dom';
import useTestStore, { Answer } from '../../stores/testStore';
import { useForm, Controller } from "react-hook-form";

type Question = {
    question: string;
    answers: Answer[];
    selectedAnswer: number | null;
}

type FormValues = {
    questions: Question[]
}

export default function TestPage() {
    const getTest = useTestStore(state => state.getTest);
    const test = useTestStore(state => state.test);
    const params = useParams();
    const testId = params.id;
    const { control, handleSubmit, setValue } = useForm<FormValues>({
        defaultValues: {
            questions: test ? test.questions.map(question => ({
                question: question.question,
                answerw: question.answers,
                selectedAnswer: null
            })) : []
        }
    });

    let result: string | undefined;

    useEffect(() => {
        if (testId) {
            getTest(testId)
        }
    }, [testId, getTest]);

    useEffect(() => {
        if (test) {
            // Установим значения по умолчанию после загрузки теста
            setValue("questions", test.questions.map(q => ({
                question: q.question,
                answers: q.answers,
                selectedAnswer: null
            })));
        }
    }, [test, setValue]);

    const onSubmit = (data: FormValues) => {
        let score = 0;
        data.questions.map(q => {
            if (q.selectedAnswer) {
                score += q.selectedAnswer;
            }
            return score;
        });
        if (test && test.results) {
            for (let i = 0; i < test.results.length; i++) {
                if (score >= test.results[i].minScore && score <= test.results[i].maxScore) {
                    result = test.results[i].result;
                    return;
                }
            }
        }
    }

    return (
        test ?
            <div className={styles.testPage} >
                <h1>{test.name}</h1>
                <p>{test.description}</p>

                <form onSubmit={handleSubmit(onSubmit)}
                >
                    {test.questions.map((question, qIndex) => (
                        <div key={qIndex} className={styles.question}>
                            <h3>{question.question}</h3>
                            {question.answers.map((answer, aIndex) => (
                                <div key={aIndex} className={styles.answer}>
                                    <Controller name={`questions.${qIndex}.selectedAnswer`}
                                        control={control}
                                        render={({ field }) => (
                                            <input
                                                type="radio"
                                                name={`question-${qIndex}`}
                                                value={answer.points}
                                                checked={String(field.value) === answer.points}
                                                onChange={() => field.onChange(answer.points)}
                                            />
                                        )}
                                    />
                                    <label>{answer.answer}</label>
                                </div>
                            ))}
                        </div>
                    ))}

                    <button type="submit">Submit</button>
                    <h6>{result && result}</h6>
                </form>

                {/*
                    score !== 0 && (
                        <div className={styles.results}>
                            <h2>Your Score: {score}</h2>
                            {test.results.map((result, index) => (
                                score >= result.minScore && score <= result.maxScore ? (
                                    <div key={index}>
                                        <h3>{result.result}</h3>
                                    </div>
                                ) : null
                            ))}
                        </div>
                    )
                */}
            </div >
            : <div className={styles.testPage}><h1>Loading...</h1></div>
    );
};
