import React, { useEffect, useState } from 'react';
import styles from './Home.module.scss';
import useHomeStore from '../../stores/homeStore';

const tests = [
    { id: 1, title: 'Тест по математике', description: 'Проверьте свои знания по математике.' },
    { id: 2, title: 'Тест по истории', description: 'Проверьте свои знания по истории.' },
    { id: 3, title: 'Тест по биологии', description: 'Проверьте свои знания по биологии.' },
    { id: 4, title: 'Тест по физике', description: 'Проверьте свои знания по физике.' },
];

const Home = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const getTestIcons = useHomeStore(state => state.getTests);
    const testIcons = useHomeStore(state => state.tests);

    const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setSearchQuery(e.target.value);
    };

    const filteredTests = tests.filter(test =>
        test.title.toLowerCase().includes(searchQuery.toLowerCase())
    );


    useEffect(() => {
        getTestIcons();
    }, [])

    return (
        <div className={styles.main}>
            {tests && <>
                <header className={styles.header}>
                    <input
                        type="text"
                        className={styles.searchInput}
                        placeholder="&#x1F50E;&#xFE0E; Search for tests..."
                        value={searchQuery}
                        onChange={(e) => handleSearchChange(e)}
                    />
                    <h1>Some tests for you :D</h1>
                </header>
                <ul className={styles.testList}>
                    {filteredTests.map(test => (
                        <li key={test.id} className={styles.testItem}>
                            <h2 className={styles.testTitle}>{test.title}</h2>
                            <p className={styles.testDescription}>{test.description}</p>
                        </li>

                    ))}
                </ul>
            </>

            }
        </div>
    );
};

export default Home;
